+const TeacherMyClassPage = React.lazy(() => import('./app/teacher/my-class/page'));
+
+import { ResetPassword } from "@/components/auth/ResetPassword";
+import { SessionDebugger } from './components/SessionDebugger';
+
+// Create a new QueryClient instance
+const queryClient = new QueryClient({
+  defaultOptions: {
+    queries: {
+      staleTime: 5 * 60 * 1000,
+      gcTime: 10 * 60 * 1000,
+    },
+  },
+});
+
+function App() {
+  const { theme, resolvedTheme } = useTheme()
+  const currentTheme = themes[theme as keyof typeof themes] || themes[resolvedTheme as keyof typeof themes] || themes.light
+  const location = useLocation();
+  const navigate = useNavigate();
+
+  // Persist route on navigation
+  useEffect(() => {
+    const currentPath = location.pathname + location.search;
+    // Only store route if it's not login and not the root path
+    if (currentPath !== '/login' && currentPath !== '/') {
+      localStorage.setItem('lastRoute', currentPath);
+      logger.debug('Route persisted', { 
+        source: 'App',
+        context: { route: currentPath }
+      });
+    }
+  }, [location]);
+
+  // Handle route restoration
+  useEffect(() => {
+    const lastRoute = localStorage.getItem('lastRoute');
+    const isInitialLoad = !location.key;
+    const isRootPath = location.pathname === '/';
+    
+    if (isInitialLoad && lastRoute && lastRoute !== '/login') {
+      // If we're at root path or dashboard, restore the last route
+      if (isRootPath || location.pathname === '/dashboard') {
+        logger.debug('Restoring route', {
+          source: 'App',
+          context: { 
+            from: location.pathname,
+            to: lastRoute
+          }
+        });
+        navigate(lastRoute, { replace: true });
+      }
+    }
+  }, [navigate, location]);
+
+  console.log('[App] Rendering...');
+  logger.info('App initializing', { source: 'App', context: { route: location.pathname } });
+
+  return (
+    <ErrorBoundary source="App">
+      <QueryClientProvider client={queryClient}>
+        <AuthProvider>
+          <ErrorProvider>
+            <NextThemesProvider
+              attribute="class"
+              defaultTheme="light"
+              value={{
+                light: "light",
+                dark: "dark",
+                gray: "gray",
+                contrast: "contrast"
+              }}
+            >
+              <ThemeContextProvider>
+                <ToastProvider>
+                <div className={`relative min-h-screen w-full ${currentTheme.background} ${currentTheme.text}`}>
+                  <ThemeToggle />
+                  <div className={`w-full ${currentTheme.background} ${currentTheme.text}`}>
+                    <Routes>
+                      <Route 
+                        path="/login" 
+                        element={
+                          <React.Suspense fallback={<LoadingSpinner message="Loading login..." />}>
+                            <div className={currentTheme.background}>
+                              <Login />
+                            </div>
+                          </React.Suspense>
+                        }
+                      />
+                      <Route element={
+                        <div className={currentTheme.background}>
+                          <PrivateRoute>
+                            <Layout />
+                          </PrivateRoute>
+                        </div>
+                      }>
+                        {/* Handle root path with conditional redirect */}
+                        <Route 
+                          path="/" 
+                          element={
+                            <RootRouteHandler />
+                          } 
+                        />
+                        <Route path="/dashboard" element={<DashboardPage />} />
+                        <Route 
+                          path="app/learning/*" 
+                          element={
+                            <React.Suspense fallback={<LoadingSpinner message="Loading learning management..." />}>
+                              <LearningManagement />
+                            </React.Suspense>
+                          } 
+                        />
+                        <Route path="analytics" element={
+                          <React.Suspense fallback={<LoadingSpinner message="Loading analytics..." />}>
+                            <Analytics />
+                          </React.Suspense>
+                        } />
+                        <Route path="schools" element={
+                          <React.Suspense fallback={<LoadingSpinner message="Loading schools..." />}>
+                            <Schools />
+                          </React.Suspense>
+                        } />
+                        <Route path="students" element={
+                          <React.Suspense fallback={<LoadingSpinner message="Loading students..." />}>
+                            <Students />
+                          </React.Suspense>
+                        } />
+                        <Route path="sales" element={
+                          <React.Suspense fallback={<LoadingSpinner message="Loading sales..." />}>
+                            <Sales />
+                          </React.Suspense>
+                        } />
+                        <Route path="content/*" element={
+                          <React.Suspense fallback={<LoadingSpinner message="Loading content..." />}>
+                            <Content />
+                          </React.Suspense>
+                        } />
+                        <Route path="development" element={
+                          <React.Suspense fallback={<LoadingSpinner message="Loading development..." />}>
+                            <Development />
+                          </React.Suspense>
+                        } />
+                        <Route path="infrastructure" element={
+                          <React.Suspense fallback={<LoadingSpinner message="Loading infrastructure..." />}>
+                            <Infrastructure />
+                          </React.Suspense>
+                        } />
+                        <Route path="finance/*" element={
+                          <React.Suspense fallback={<LoadingSpinner message="Loading finance..." />}>
+                            <Finance />
+                          </React.Suspense>
+                        } />
+                        <Route path="staff" element={
+                          <React.Suspense fallback={<LoadingSpinner message="Loading staff..." />}>
+                            <Staff />
+                          </React.Suspense>
+                        } />
+                        <Route path="schedule" element={
+                          <React.Suspense fallback={<LoadingSpinner message="Loading schedule..." />}>
+                            <Schedule />
+                          </React.Suspense>
+                        } />
+                        <Route path="reports" element={
+                          <React.Suspense fallback={<LoadingSpinner message="Loading reports..." />}>
+                            <Reports />
+                          </React.Suspense>
+                        } />
+                        <Route path="events" element={
+                          <React.Suspense fallback={<LoadingSpinner message="Loading events..." />}>
+                            <Events />
+                          </React.Suspense>
+                        } />
+                        <Route path="settings" element={
+                          <React.Suspense fallback={<LoadingSpinner message="Loading settings..." />}>
+                            <Settings />
+                          </React.Suspense>
+                        } />
+                        <Route path="error-test" element={
+                          <React.Suspense fallback={<LoadingSpinner message="Loading error test..." />}>
+                            <ErrorTest />
+                          </React.Suspense>
+                        } />
+                        <Route 
+                          path="content-management" 
+                          element={
+                            <React.Suspense fallback={<LoadingSpinner message="Loading content management..." />}>
+                              <ContentManagementPage />
+                            </React.Suspense>
+                          } 
+                        />
+                        <Route
+                          path="my-classes" 
+                          element={
+                            <React.Suspense fallback={<LoadingSpinner message="Loading My Classes..." />}>
+                              <MyClasses/>
+                            </React.Suspense>
+                          } 
+                        />
+                      </Route>
+                      <Route 
+                        path="/teacher/*" 
+                        element={
+                          <TeacherLayout>
+                            <Routes>
+                              <Route path="/" element={<Navigate to="dashboard" replace />} />
+                              <Route path="dashboard" element={<TeacherDashboard />} />
+                              <Route path="classes" element={<Classes />} />
+                              <Route path="lessons" element={<Classes />} />
+                              <Route path="my-class" element={
+                                <React.Suspense fallback={<LoadingSpinner message="Loading My Class..." />}>
+                                  <TeacherMyClassPage />
+                                </React.Suspense>
+                              } />
+                              <Route path="AI-Conversation" element={<AIConversationPage />} />
+                            </Routes>
+                          </TeacherLayout>
+                        } 
+                      />
+                      <Route 
+                        path="/reset-password/*" 
+                        element={<ResetPassword />} 
+                      />
+                      <Route path="*" element={<Navigate to="/dashboard" replace />} />
+                    </Routes>
+                    <ErrorToast />
+                    {process.env.NODE_ENV === 'development' && (
+                      <React.Suspense fallback={null}>
+                        <ReactQueryDevtools initialIsOpen={false} />
+                      </React.Suspense>
+                    )}
+                  </div>
+                </div>
+                <Toaster />
+                {import.meta.env.DEV && <SessionDebugger />}
+              </ToastProvider>
+            </ThemeContextProvider>
+            </NextThemesProvider>
+          </ErrorProvider>
+        </AuthProvider>
+      </QueryClientProvider>
+    </ErrorBoundary>
+  );
+}
+
+// Add this component to handle root path routing
+function RootRouteHandler() {
+  const navigate = useNavigate();
+  const location = useLocation();
+
+  useEffect(() => {
+    const lastRoute = localStorage.getItem('lastRoute');
+    
+    if (lastRoute && lastRoute !== '/login' && lastRoute !== '/') {
+      logger.debug('Root path: restoring last route', {
+        source: 'RootRouteHandler',
+        context: { lastRoute }
+      });
+      navigate(lastRoute, { replace: true });
+    } else {
+      logger.debug('Root path: redirecting to dashboard', {
+        source: 'RootRouteHandler'
+      });
+      navigate('/dashboard', { replace: true });
+    }
+  }, [navigate]);
+
+  return <LoadingSpinner message="Restoring your last location..." />;
+}
+
+export default App;

