+};
+
+// Enhanced logging utility
+const forceLog = (message: string, data?: any, type: 'info' | 'warn' | 'error' = 'info') => {
+  if (!DEBUG) return;
+  
+  const timestamp = new Date().toISOString();
+  const logEntry = {
+    timestamp,
+    message,
+    data,
+    type,
+    url: window.location.href,
+    pathname: window.location.pathname,
+    search: window.location.search
+  };
+  
+  // Console logging with visual distinction
+  const styles = {
+    info: 'color: #4CAF50; font-weight: bold;',
+    warn: 'color: #FFC107; font-weight: bold;',
+    error: 'color: #F44336; font-weight: bold;'
+  };
+  
+  console.log(`%c[${timestamp}] ${message}`, styles[type]);
+  if (data) {
+    console.log('Data:', data);
+    if (typeof data === 'object') {
+      console.table(data);
+    }
+  }
+  
+  // Backup to localStorage
+  if (LOG_TO_STORAGE) {
+    try {
+      const logs = JSON.parse(localStorage.getItem(STORAGE_KEY) || '[]');
+      logs.push(logEntry);
+      localStorage.setItem(STORAGE_KEY, JSON.stringify(logs.slice(-100))); // Keep last 100 logs
+    } catch (e) {
+      console.warn('Failed to save log to localStorage:', e);
+    }
+  }
+};
