+  const startTime = performance.now();
+  const logger = createLogger('fetchLessonData');
+  
+  logger.info('Starting lesson data fetch', { 
+    lessonId, 
+    topicId, 
+    subtopicId,
+    cacheKey: `lesson_state_${lessonId}`
+  });
+
+  try {
+    // Check cache first
+    const cachedData = localStorage.getItem(`lesson_state_${lessonId}`);
+    if (cachedData) {
+      try {
+        const parsed = JSON.parse(cachedData);
+        const cacheAge = Date.now() - (parsed.timestamp || 0);
+        
+        // Use cache if it's less than 5 minutes old
+        if (cacheAge < 5 * 60 * 1000) {
+          logger.info('Using cached lesson data', { 
+            cacheAge: `${(cacheAge / 1000).toFixed(2)}s`,
+            dataSize: cachedData.length
+          });
+          return parsed.data;
+        }
+      } catch (e) {
+        logger.warn('Failed to parse cached data', e);
+      }
+    }
+
+    // Fetch fresh data with performance tracking
+    const fetchStart = performance.now();
+    
+    const [lessonResult, topicResult, subtopicResult] = await Promise.all([
+      supabase
+        .from('lessons')
+        .select(`
+          id,
+          title,
+          name,
+          content,
+          description,
+          topic_id,
+          subtopic_id,
+          questions:questions (
+            id,
+            title,
+            content,
+            type,
+            data,
+            metadata,
+            points
+          ),
+          activities:activities (
+            id,
+            title,
+            description,
+            type,
+            data,
+            lesson_id,
+            name,
+            created_at,
+            updated_at,
+            metadata
+          ),
+          exercise_prompts:exercise_prompts (
+            id,
+            content,
+            type,
+            metadata,
+            text,
+            created_at,
+            updated_at,
+            contentId,
+            lesson_id,
+            order,
+            status,
+            difficulty
+          )
+        `)
+        .eq('id', lessonId)
+        .single(),
+      
+      supabase
+        .from('topics')
+        .select('id, title')
+        .eq('id', topicId)
+        .single(),
+      
+      supabase
+        .from('subtopics')
+        .select('id, title')
+        .eq('id', subtopicId)
+        .single()
+    ]);
+
+    const fetchEnd = performance.now();
+    logger.info('Database queries completed', {
+      duration: `${(fetchEnd - fetchStart).toFixed(2)}ms`,
+      lessonDataSize: JSON.stringify(lessonResult.data).length
+    });
+
+    // Validate responses
+    if (lessonResult.error) throw new Error(`Lesson fetch failed: ${lessonResult.error.message}`);
+    if (topicResult.error) throw new Error(`Topic fetch failed: ${topicResult.error.message}`);
+    if (subtopicResult.error) throw new Error(`Subtopic fetch failed: ${subtopicResult.error.message}`);
+
+    // Validate required data
+    if (!lessonResult.data || !topicResult.data || !subtopicResult.data) {
+      throw new Error('Missing required data from database');
+    }
+
+    // Type assertion for the database results
+    const activities = (lessonResult.data.activities || []) as Activity[];
+    const exercisePrompts = (lessonResult.data.exercise_prompts || []) as ExercisePrompt[];
+
+    const lessonState: LessonState = {
+      lesson: {
+        ...lessonResult.data,
+        questions: lessonResult.data.questions || [],
+        activities,
+        exercise_prompts: exercisePrompts
+      },
+      topic: topicResult.data,
+      subtopic: subtopicResult.data
+    };
+
+    // Cache the result with timestamp
+    const cacheData = {
+      data: lessonState,
+      timestamp: Date.now()
+    };
+    localStorage.setItem(`lesson_state_${lessonId}`, JSON.stringify(cacheData));
+
+    const endTime = performance.now();
+    logger.info('Lesson data fetch completed', {
+      totalDuration: `${(endTime - startTime).toFixed(2)}ms`,
+      questionCount: lessonState.lesson.questions.length,
+      activityCount: activities.length,
+      promptCount: exercisePrompts.length
+    });
+
+    return lessonState;
+
+  } catch (error) {
+    const endTime = performance.now();
+    logger.error('Failed to fetch lesson data', {
+      error,
+      duration: `${(endTime - startTime).toFixed(2)}ms`
+    });
+    throw error;
+  }
+};
