"use client";

import { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { motion, AnimatePresence } from "framer-motion";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Progress } from "@/components/ui/progress";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { ArrowLeft, BookOpen, CheckCircle2, Clock, Flag, HelpCircle, Layout, List, Play, Settings, Star, X } from "lucide-react";
import { cn } from "@/lib/utils";
import { supabase } from "@/lib/supabase";
import { LessonWithContent } from "@/types";

export default function LessonPage() {
  const { lessonId } = useParams();
  const navigate = useNavigate();
  const [lesson, setLesson] = useState<LessonWithContent | null>(null);
  const [currentStep, setCurrentStep] = useState(0);
  const [progress, setProgress] = useState(0);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchLesson = async () => {
      try {
        const { data, error } = await supabase
          .from('lessons')
          .select(`
            *,
            questions (*),
            activities (*),
            exercise_prompts (*)
          `)
          .eq('id', lessonId)
          .single();

        if (error) throw error;
        setLesson(data);
      } catch (error) {
        console.error('Error fetching lesson:', error);
      } finally {
        setIsLoading(false);
      }
    };

    fetchLesson();
  }, [lessonId]);

  const handleEndLesson = () => {
    navigate('/teacher/lessons');
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary"></div>
      </div>
    );
  }

  if (!lesson) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <Card className="p-6">
          <CardTitle className="text-xl mb-4">Lesson not found</CardTitle>
          <Button onClick={() => navigate('/teacher/lessons')}>Go Back</Button>
        </Card>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-b from-background to-accent/20">
      {/* Top Navigation Bar */}
      <nav className="fixed top-0 left-0 right-0 z-50 bg-background/95 backdrop-blur supports-[backdrop-filter]:bg-background/60 border-b">
        <div className="flex items-center justify-between p-4">
          <div className="flex items-center gap-4">
            <Button
              variant="ghost"
              size="icon"
              onClick={handleEndLesson}
              className="hover:bg-accent"
            >
              <X className="h-5 w-5" />
            </Button>
            <div>
              <h1 className="text-lg font-semibold">{lesson.title}</h1>
              <p className="text-sm text-muted-foreground">Lesson {currentStep + 1} of {lesson.questions?.length || 0}</p>
            </div>
          </div>
          <div className="flex items-center gap-2">
            <Progress value={progress} className="w-40" />
            <Button variant="outline" onClick={handleEndLesson}>
              End Lesson
            </Button>
          </div>
        </div>
      </nav>

      {/* Main Content */}
      <main className="pt-24 pb-8 px-4 md:px-8 max-w-7xl mx-auto">
        <div className="grid grid-cols-1 lg:grid-cols-[300px_1fr] gap-8">
          {/* Sidebar */}
          <div className="lg:block">
            <Card className="sticky top-24">
              <CardHeader>
                <CardTitle className="text-lg flex items-center gap-2">
                  <List className="h-5 w-5" />
                  Lesson Overview
                </CardTitle>
              </CardHeader>
              <CardContent>
                <div className="space-y-4">
                  <div className="flex items-center gap-2 text-sm">
                    <Clock className="h-4 w-4 text-primary" />
                    <span>{lesson.duration || 30} minutes</span>
                  </div>
                  <div className="flex items-center gap-2 text-sm">
                    <HelpCircle className="h-4 w-4 text-primary" />
                    <span>{lesson.questions?.length || 0} Questions</span>
                  </div>
                  <div className="flex items-center gap-2 text-sm">
                    <Layout className="h-4 w-4 text-primary" />
                    <span>{lesson.activities?.length || 0} Activities</span>
                  </div>
                  
                  <div className="border-t pt-4 mt-4">
                    <h3 className="font-medium mb-3">Progress</h3>
                    <div className="space-y-3">
                      {lesson.questions?.map((_, index) => (
                        <div
                          key={index}
                          className={cn(
                            "flex items-center gap-2 p-2 rounded-md transition-colors",
                            currentStep === index ? "bg-accent" : "hover:bg-accent/50",
                            index < currentStep && "text-primary"
                          )}
                        >
                          {index < currentStep ? (
                            <CheckCircle2 className="h-4 w-4" />
                          ) : (
                            <div className={cn(
                              "h-4 w-4 rounded-full border",
                              currentStep === index && "border-primary"
                            )} />
                          )}
                          <span className="text-sm">Question {index + 1}</span>
                        </div>
                      ))}
                    </div>
                  </div>
                </div>
              </CardContent>
            </Card>
          </div>

          {/* Main Content Area */}
          <div className="space-y-8">
            <AnimatePresence mode="wait">
              <motion.div
                key={currentStep}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -20 }}
                transition={{ duration: 0.3 }}
              >
                <Card>
                  <CardHeader>
                    <div className="flex items-center justify-between">
                      <Badge variant="outline" className="bg-primary/5">
                        Question {currentStep + 1}
                      </Badge>
                      <div className="flex items-center gap-2">
                        <Star className="h-4 w-4 text-yellow-500" />
                        <span className="text-sm">Points: 10</span>
                      </div>
                    </div>
                    <CardTitle className="text-2xl mt-4">
                      {lesson.questions?.[currentStep]?.title || "Question Title"}
                    </CardTitle>
                  </CardHeader>
                  <CardContent className="space-y-6">
                    {/* Question Content */}
                    <div className="prose prose-sm dark:prose-invert max-w-none">
                      {lesson.questions?.[currentStep]?.content || "Question content goes here..."}
                    </div>

                    {/* Interactive Elements */}
                    <div className="space-y-4 pt-6">
                      <Button 
                        className="w-full justify-start text-left"
                        variant="outline"
                      >
                        <Play className="h-4 w-4 mr-2" />
                        Start Activity
                      </Button>
                    </div>
                  </CardContent>
                </Card>
              </motion.div>
            </AnimatePresence>

            {/* Navigation Buttons */}
            <div className="flex items-center justify-between pt-4">
              <Button
                variant="outline"
                onClick={() => setCurrentStep(prev => Math.max(0, prev - 1))}
                disabled={currentStep === 0}
              >
                <ArrowLeft className="h-4 w-4 mr-2" />
                Previous
              </Button>
              <Button
                onClick={() => {
                  if (currentStep < (lesson.questions?.length || 0) - 1) {
                    setCurrentStep(prev => prev + 1);
                    setProgress(prev => prev + (100 / (lesson.questions?.length || 1)));
                  } else {
                    handleEndLesson();
                  }
                }}
              >
                {currentStep < (lesson.questions?.length || 0) - 1 ? (
                  <>
                    Next
                    <ArrowLeft className="h-4 w-4 ml-2 rotate-180" />
                  </>
                ) : (
                  <>
                    Complete Lesson
                    <Flag className="h-4 w-4 ml-2" />
                  </>
                )}
              </Button>
            </div>
          </div>
        </div>
      </main>
    </div>
  );
}
