-  while (retryCount < maxRetries) {
-    try {
-      const storedAuth = localStorage.getItem(authStateKey);
-      if (!storedAuth) {
-        forceLog('[Auth] No stored auth state found', { authStateKey, attempt: retryCount + 1 }, 'error');
-        throw new Error('No stored auth state found');
