import { useSessionMonitoring } from '../hooks/useSessionMonitoring';
import { useEffect } from 'react';
import { logger } from '../lib/logger';

export function SessionDebugger() {
  const { 
    isAuthenticated, 
    currentRole, 
    lastActivity, 
    sessionErrors,
    hasErrors 
  } = useSessionMonitoring();

  useEffect(() => {
    if (hasErrors) {
      logger.error('Session errors detected', {
        context: { sessionErrors },
        source: 'SessionDebugger'
      });
    }
  }, [hasErrors, sessionErrors]);

  if (import.meta.env.DEV) {
    return (
      <div className="fixed bottom-4 right-4 p-4 bg-gray-800 text-white rounded-lg shadow-lg max-w-sm opacity-75 hover:opacity-100 transition-opacity">
        <h3 className="font-bold mb-2">Session Debug</h3>
        <div className="space-y-1 text-sm">
          <p>Status: {isAuthenticated ? 'üü¢ Active' : 'üî¥ Inactive'}</p>
          <p>Role: {currentRole || 'None'}</p>
          <p>Last Activity: {lastActivity.toLocaleTimeString()}</p>
          {hasErrors && (
            <div className="mt-2">
              <p className="font-bold text-red-400">Errors:</p>
              <ul className="list-disc pl-4">
                {sessionErrors.map((error, i) => (
                  <li key={i} className="text-red-400">{error}</li>
                ))}
              </ul>
            </div>
          )}
        </div>
      </div>
    );
  }

  return null;
}
