+"use client"
+
+import React from 'react';
+import { Dialog, DialogContent } from "@/components/ui/dialog";
+import { Button } from "@/components/ui/button";
+import { LoadingSpinner } from "@/components/LoadingSpinner";
+import { RefreshCw } from 'lucide-react';
+
+interface LessonDialogProps {
+  isOpen: boolean;
+  onClose: () => void;
+  lessonContent: {
+    content: any;
+    isLoading: boolean;
+    error: string | null;
+  };
+  currentLessonId: string | null;
+  onRetry: () => void;
+}
+
+export function LessonDialog({
+  isOpen,
+  onClose,
+  lessonContent,
+  currentLessonId,
+  onRetry
+}: LessonDialogProps) {
+  return (
+    <Dialog open={isOpen} onOpenChange={onClose}>
+      <DialogContent 
+        className="max-w-4xl h-[90vh] p-0"
+        aria-describedby="lesson-content-description"
+      >
+        <div className="h-full flex flex-col">
+          {lessonContent.isLoading ? (
+            <div className="flex-1 flex items-center justify-center">
+              <LoadingSpinner />
+            </div>
+          ) : lessonContent.error ? (
+            <div className="flex-1 flex flex-col items-center justify-center gap-4 p-6">
+              <p className="text-destructive">{lessonContent.error}</p>
+              <Button onClick={onRetry} variant="outline">
+                <RefreshCw className="h-4 w-4 mr-2" />
+                Retry Loading
+              </Button>
+            </div>
+          ) : (
+            <div className="flex-1 overflow-y-auto p-6">
+              {/* Render lesson content here */}
+              <div id="lesson-content-description">
+                {lessonContent.content && (
+                  <div className="prose prose-sm max-w-none dark:prose-invert">
+                    <h1>{lessonContent.content.title}</h1>
+                    <div dangerouslySetInnerHTML={{ __html: lessonContent.content.content || '' }} />
+                  </div>
+                )}
+              </div>
+            </div>
+          )}
+        </div>
+      </DialogContent>
+    </Dialog>
+  );
+} 
