"use client"

import * as React from "react"
import { toast as foxToast } from 'react-fox-toast'
import { cn } from "@/lib/utils"

// Keep existing types for backward compatibility
export type {
  ToastProps,
  ToastActionElement,
}

// Maintain existing interface but use react-fox-toast internally
export const toast = {
  success: (message: string, options?: any) => {
    return foxToast.success(message, {
      position: 'bottom-right',
      ...options
    })
  },
  error: (message: string, options?: any) => {
    return foxToast.error(message, {
      position: 'bottom-right',
      ...options
    })
  },
  info: (message: string, options?: any) => {
    return foxToast.info(message, {
      position: 'bottom-right',
      ...options
    })
  },
  warning: (message: string, options?: any) => {
    return foxToast.warning(message, {
      position: 'bottom-right',
      ...options
    })
  },
  custom: (jsx: React.ReactNode, options?: any) => {
    return foxToast(jsx, {
      position: 'bottom-right',
      ...options
    })
  }
}

// Export a hook that maintains the same interface
export function useToast() {
  return {
    toast,
    dismiss: foxToast.dismiss
  }
}

// Re-export components that might still be used in the codebase
export {
  ToastProvider,
  ToastViewport,
  Toast,
  ToastTitle,
  ToastDescription,
  ToastClose,
  ToastAction,
} from 'react-fox-toast'
