export interface Grade {
  _id: string;
  id: string;
  name: string;
  level: number;
  orderIndex: number;
  topics: Topic[];
}

export interface Topic {
  _id: string;
  id: string;
  name: string;
  title: string;
  grade_id: string;
  subtopics: Subtopic[];
}

export interface Subtopic {
  id: string;
  title: string;
  description?: string;
  topic_id: string;
  lessons: Lesson[];
  order_index: number;
}

export interface Lesson {
  id: string;
  title: string;
  content?: string;
  metadata?: LessonMetadata;
  content_type?: string;
  lesson_type?: string;
  grade_id?: string;
  topic_id?: string;
  subtopic_id: string;
  user_id?: string;
  updated_at?: string;
  duration?: number;
  description?: string;
  prerequisites?: string[];
  media_type?: string;
  media_url?: string;
  contentheading?: string;
  questions?: Question[];
}

export interface LessonMetadata {
  lastEdited?: string;
  version?: number;
  tags?: string[];
  status?: 'draft' | 'published';
  authorId?: string;
  duration?: number;
  transcript?: string;
}

export interface Question {
  id: string;
  type: string;
  lesson_id: string;
  title: string;
  content: string;
  metadata: Record<string, any>;
  data: QuestionData;
  correct_answer: string;
  exercisePrompts: ExercisePrompt[];
  isDraft?: boolean;
}

export interface QuestionData {
  prompt?: string;
  teacher_script?: string;
  followup_prompt?: string[];
  sample_answer?: string;
  answer?: string;
  options?: string[];
  correct_answer?: string;
  explanation?: string;
  [key: string]: any;
}

export interface ExercisePrompt {
  id: string;
  question_id?: string;
  text: string;
  media: string | MediaItem[];
  type: 'image' | 'gif' | 'video';
  narration: string;
  saytext: string;
  metadata?: {
    estimatedTime?: number;
    [key: string]: any;
  };
  created_at?: string;
  updated_at?: string;
}

export interface MediaItem {
  type: 'image' | 'gif' | 'video';
  url: string;
}

export interface Activity {
  id: string;
  lesson_id: string;
  created_at: string;
  type: string;
  title: string;
  name: string;
  description: string;
  instructions: string;
  media: MediaItem[];
  data: {
    prompt: string;
    teacher_script: string;
    media: MediaItem[];
    [key: string]: any;
  };
}

export interface SaveStatus {
  id: string;
  status: 'draft' | 'saved' | 'saving' | 'error';
  lastSaved?: string;
}
