import { Check, X } from 'lucide-react';
import { cn } from '@/lib/utils';

interface ProgressIndicatorProps {
  status: 'idle' | 'saving' | 'saved' | 'error';
  className?: string;
}

export const ProgressIndicator = ({
  status,
  className
}: ProgressIndicatorProps) => {
  return (
    <div
      className={cn(
        "fixed bottom-4 right-4 px-4 py-2 rounded-lg shadow-lg transition-all duration-200",
        status === 'saving' && "bg-background border",
        status === 'saved' && "bg-green-500 text-white",
        status === 'error' && "bg-destructive text-destructive-foreground",
        className
      )}
      role="status"
      aria-live="polite"
    >
      {status === 'saving' && (
        <div className="flex items-center gap-2">
          <div 
            className="animate-spin rounded-full h-4 w-4 border-b-2 border-primary"
            aria-hidden="true"
          />
          <span>Saving changes...</span>
        </div>
      )}
      {status === 'saved' && (
        <div className="flex items-center gap-2">
          <Check className="h-4 w-4" aria-hidden="true" />
          <span>Changes saved</span>
        </div>
      )}
      {status === 'error' && (
        <div className="flex items-center gap-2">
          <X className="h-4 w-4" aria-hidden="true" />
          <span>Error saving changes</span>
        </div>
      )}
    </div>
  );
};
