import { supabase } from '../supabase';
import { logger } from '../logger';
import { errorTracker } from '../errorTracker';

interface SessionState {
  isAuthenticated: boolean;
  lastActivity: Date;
  currentRole?: string;
  sessionErrors: string[];
}

export class SessionMonitor {
  private static instance: SessionMonitor;
  private state: SessionState = {
    isAuthenticated: false,
    lastActivity: new Date(),
    sessionErrors: []
  };
  private listeners: Set<(state: SessionState) => void> = new Set();

  private constructor() {
    this.initializeMonitoring();
  }

  public static getInstance(): SessionMonitor {
    if (!SessionMonitor.instance) {
      SessionMonitor.instance = new SessionMonitor();
    }
    return SessionMonitor.instance;
  }

  private async initializeMonitoring() {
    // Monitor auth state changes
    supabase.auth.onAuthStateChange((event, session) => {
      logger.info(`Auth state changed: ${event}`, {
        context: {
          event,
          userId: session?.user?.id,
          role: session?.user?.role
        },
        source: 'SessionMonitor'
      });

      this.updateState({
        isAuthenticated: !!session,
        currentRole: session?.user?.role as string,
        lastActivity: new Date()
      });

      if (event === 'SIGNED_OUT') {
        this.handleSignOut();
      }
    });

    // Periodic session check
    setInterval(() => this.checkSession(), 30000);
  }

  private async checkSession() {
    try {
      const { data: { session }, error } = await supabase.auth.getSession();
      
      if (error) {
        this.recordSessionError(`Session check failed: ${error.message}`);
        return;
      }

      if (!session && this.state.isAuthenticated) {
        this.recordSessionError('Session lost unexpectedly');
        this.handleSessionLoss();
      }

      this.updateState({
        isAuthenticated: !!session,
        currentRole: session?.user?.role as string,
        lastActivity: new Date()
      });

    } catch (err) {
      this.recordSessionError(`Session check error: ${err instanceof Error ? err.message : String(err)}`);
    }
  }

  private handleSessionLoss() {
    logger.error('Unexpected session loss detected', {
      context: { 
        lastActivity: this.state.lastActivity,
        currentRole: this.state.currentRole
      },
      source: 'SessionMonitor'
    });

    errorTracker.trackError({
      message: 'Unexpected session loss',
      severity: 'error',
      context: {
        lastActivity: this.state.lastActivity,
        currentRole: this.state.currentRole
      },
      source: 'SessionMonitor'
    });
  }

  private handleSignOut() {
    this.state.sessionErrors = [];
    logger.info('User signed out', {
      source: 'SessionMonitor'
    });
  }

  private recordSessionError(error: string) {
    this.state.sessionErrors = [
      ...this.state.sessionErrors,
      error
    ].slice(-5); // Keep last 5 errors

    this.notifyListeners();
  }

  private updateState(updates: Partial<SessionState>) {
    this.state = { ...this.state, ...updates };
    this.notifyListeners();
  }

  public addListener(listener: (state: SessionState) => void): () => void {
    this.listeners.add(listener);
    listener({ ...this.state });
    return () => this.listeners.delete(listener);
  }

  private notifyListeners() {
    this.listeners.forEach(listener => listener({ ...this.state }));
  }

  public getState(): SessionState {
    return { ...this.state };
  }

  public getSessionErrors(): string[] {
    return [...this.state.sessionErrors];
  }
}

export const sessionMonitor = SessionMonitor.getInstance();
