import { supabase } from '../supabase';
import { sessionMonitor } from './SessionMonitor';
import { logger } from '../logger';
import { errorTracker } from '../errorTracker';
import type { UserRole } from '../../types/roles';

export class AuthError extends Error {
  constructor(message: string, public code?: string) {
    super(message);
    this.name = 'AuthError';
  }
}

export const signIn = async (email: string, password: string, role?: string) => {
  try {
    const { data: { session }, error } = await supabase.auth.signInWithPassword({ 
      email,
      password
    }); 

    if (error) throw new AuthError(error.message, error.code);
    if (!session) throw new AuthError('No session created');

    // Set initial role if provided
    if (role) {
      const { error: updateError } = await supabase.auth.updateUser({
        data: { role }
      });

      if (updateError) throw new AuthError('Failed to set initial role', updateError.code);
    }

    logger.info('Sign in successful', {
      context: { email, role },
      source: 'Auth'
    });
    
    return { session };
  } catch (err) {
    logger.error('Sign in failed', {
      context: { error: err },
      source: 'Auth'
    });
    throw err;
  }
};

export const signOut = async () => {
  try {
    const { error } = await supabase.auth.signOut();
    if (error) throw error;
    
    logger.info('Sign out successful', {
      source: 'Auth'
    });
  } catch (err) {
    logger.error('Sign out failed', {
      context: { error: err },
      source: 'Auth'
    });
    throw err;
  }
};

export async function getCurrentSession() {
  try {
    const { data: { session }, error } = await supabase.auth.getSession();
    
    if (error) {
      throw error;
    }

    return session;
  } catch (err) {
    logger.error('Failed to get current session', {
      context: { error: err },
      source: 'auth.utils'
    });
    return null;
  }
}

export async function updateUserRole(userId: string, newRole: string) {
  try {
    // Log the attempt
    logger.info('Attempting role update', {
      context: { userId, newRole },
      source: 'auth.utils'
    });

    const { error } = await supabase.auth.admin.updateUserById(
      userId,
      { user_metadata: { role: newRole } }
    );

    if (error) throw error;

    // Force session refresh
    await supabase.auth.refreshSession();
    
    // Verify role update
    const { data: { session } } = await supabase.auth.getSession();
    
    if (session?.user?.role !== newRole) {
      throw new Error('Role update verification failed');
    }

    return true;
  } catch (err) {
    errorTracker.trackError({
      message: 'Role update failed',
      severity: 'error',
      context: { userId, newRole, error: err },
      source: 'auth.utils'
    });
    throw err;
  }
}
