import type { ErrorEvent } from './types';

export class ErrorResolver {
  private resolvedErrors: Map<string, string> = new Map();

  public resolveError(error: ErrorEvent, resolution: string): void {
    this.resolvedErrors.set(error.id, resolution);
  }

  public isResolved(errorId: string): boolean {
    return this.resolvedErrors.has(errorId);
  }

  public getResolution(errorId: string): string | undefined {
    return this.resolvedErrors.get(errorId);
  }

  public clearResolutions(): void {
    this.resolvedErrors.clear();
  }
}

export const errorResolver = new ErrorResolver();
