// src/scripts/insert-pp1-data.ts
import { createClient } from '@supabase/supabase-js'
import { v4 as uuidv4 } from 'uuid'
import pp1Data from '../data/pp1_data.json'

// Define types based on your schema
interface ExercisePrompt {
  id: string
  text: string
  narration: string
  saytext: string
  order_index: number
  question_id: string
}

interface Question {
  id: string
  title: string
  content: string
  type: string
  exercise_prompts: ExercisePrompt[]
  lesson_id: string
  order_index: number
}

interface Activity {
  id: string
  title: string
  type: string
  description: string
  instructions: string
  duration: number
  lesson_id: string
}

interface Lesson {
  id: string
  title: string
  description: string
  content_type: string
  duration: number
  difficulty: string
  metadata: Record<string, any>
  questions: Question[]
  activities: Activity[]
  subtopic_id: string
  order_index: number
}

interface Subtopic {
  id: string
  title: string
  description: string
  order_index: number
  lessons: Lesson[]
  topic_id: string
}

interface Topic {
  id: string
  title: string
  description: string
  subtopics: Subtopic[]
  grade_id: string
  order_index: number
}

// Initialize Supabase client
const supabaseUrl = process.env.NEXT_PUBLIC_SUPABASE_URL!
const supabaseKey = process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY!
const supabase = createClient(supabaseUrl, supabaseKey)

async function insertPP1Data() {
  try {
    // Start a transaction
    const { data: gradeData, error: gradeError } = await supabase
      .from('grades')
      .insert({
        id: uuidv4(),
        name: 'PP1',
        level: 1
      })
      .select('id')
      .single()

    if (gradeError) throw gradeError
    const gradeId = gradeData.id

    // Process each topic
    for (const topic of pp1Data.topics) {
      const topicId = uuidv4()
      const { error: topicError } = await supabase
        .from('topics')
        .insert({
          id: topicId,
          title: topic.title || `Topic ${topic.id}`,
          description: topic.description || '',
          grade_id: gradeId,
          order_index: topic.order_index || 1
        })

      if (topicError) throw topicError

      // Process subtopics
      for (const subtopic of topic.subtopics) {
        const subtopicId = uuidv4()
        const { error: subtopicError } = await supabase
          .from('subtopics')
          .insert({
            id: subtopicId,
            title: subtopic.title,
            description: subtopic.description,
            topic_id: topicId,
            order_index: subtopic.order_index
          })

        if (subtopicError) throw subtopicError

        // Process lessons
        for (const lesson of subtopic.lessons) {
          const lessonId = uuidv4()
          const { error: lessonError } = await supabase
            .from('lessons')
            .insert({
              id: lessonId,
              title: lesson.title,
              description: lesson.description,
              content_type: lesson.content_type,
              duration: lesson.duration,
              difficulty: lesson.difficulty,
              metadata: lesson.metadata,
              subtopic_id: subtopicId,
              order_index: lesson.order_index
            })

          if (lessonError) throw lessonError

          // Process questions
          for (const question of lesson.questions) {
            const questionId = uuidv4()
            const { error: questionError } = await supabase
              .from('questions')
              .insert({
                id: questionId,
                title: question.title,
                content: question.content,
                type: question.type,
                lesson_id: lessonId,
                order_index: question.order_index
              })

            if (questionError) throw questionError

            // Process exercise prompts
            const exercisePrompts = question.exercise_prompts.map(prompt => ({
              id: uuidv4(),
              text: prompt.text,
              narration: prompt.narration,
              saytext: prompt.saytext,
              order_index: prompt.order_index,
              question_id: questionId
            }))

            const { error: promptError } = await supabase
              .from('exercise_prompts')
              .insert(exercisePrompts)

            if (promptError) throw promptError
          }

          // Process activities
          if (lesson.activities && lesson.activities.length > 0) {
            const activities = lesson.activities.map(activity => ({
              id: uuidv4(),
              title: activity.title,
              type: activity.type,
              description: activity.description,
              instructions: activity.instructions,
              duration: activity.duration,
              lesson_id: lessonId
            }))

            const { error: activityError } = await supabase
              .from('activities')
              .insert(activities)

            if (activityError) throw activityError
          }
        }
      }
    }

    console.log('Data insertion completed successfully!')
  } catch (error) {
    console.error('Error inserting data:', error)
    throw error
  }
