const LOG_THROTTLE_MS = 5000; // 5 seconds
const lastLogTimes = new Map<string, number>();

function shouldLog(source: string, level: string): boolean {
  // Always log errors and warnings
  if (level === 'error' || level === 'warn') return true;
  
  // Throttle auth-related logs
  if (source.includes('Auth') || source.includes('Session')) {
    const now = Date.now();
    const lastTime = lastLogTimes.get(source) || 0;
    
    if (now - lastTime < LOG_THROTTLE_MS) {
      return false;
    }
    
    lastLogTimes.set(source, now);
  }
  
  return true;
}

export const logger = {
  debug: (message: string, source: string) => {
    if (shouldLog(source, 'debug')) {
      console.debug(`[${new Date().toISOString()}] ${message} [DEBUG] üîçüêû [${source}]`);
    }
  },
  // ... other logger methods
};
